import React, { useState } from 'react';
import { submitGrievance } from '../services/api';
import '../styles/Login.css';  // Adjust the path as necessary

const GrievanceForm = () => {
  const [formData, setFormData] = useState({
    user_id: '',
    type: '',
    title: '',
    description: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    submitGrievance(formData, token)
      .then((response) => {
        alert('Grievance submitted successfully');
      })
      .catch((error) => {
        console.error('Error submitting grievance:', error);
        alert(`Error submitting grievance: ${error.response?.data?.message || 'Server error'}`);
      });
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Submit Grievance</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.formGroup}>
          <label style={styles.label}>User ID</label>
          <input
            type="text"
            name="user_id"
            style={styles.input}
            placeholder="User ID"
            onChange={handleChange}
            required
          />
        </div>
        <div style={styles.formGroup}>
          <label style={styles.label}>Type</label>
          <input
            type="text"
            name="type"
            style={styles.input}
            placeholder="Type"
            onChange={handleChange}
            required
          />
        </div>
        <div style={styles.formGroup}>
          <label style={styles.label}>Title</label>
          <input
            type="text"
            name="title"
            style={styles.input}
            placeholder="Title"
            onChange={handleChange}
            required
          />
        </div>
        <div style={styles.formGroup}>
          <label style={styles.label}>Description</label>
          <textarea
            name="description"
            style={{ ...styles.input, ...styles.textarea }}
            placeholder="Description"
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" style={styles.button}>Submit Grievance</button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '500px',
    margin: '0 auto',
    padding: '40px',
    backgroundColor: 'rgba(0, 0, 0, 0.85)',
    borderRadius: '8px',
    boxShadow: '0px 10px 30px rgba(0, 0, 0, 0.5)',
    textAlign: 'center',
    color: '#fff',
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  formGroup: {
    marginBottom: '20px',
  },
  label: {
    display: 'block',
    marginBottom: '8px',
    fontWeight: 'bold',
    textAlign: 'left',
  },
  input: {
    width: '100%',
    padding: '10px',
    borderRadius: '4px',
    border: 'none',
    backgroundColor: '#222',
    color: '#fff',
  },
  textarea: {
    height: '100px',
    resize: 'none',
  },
  button: {
    width: '100%',
    padding: '12px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '16px',
    transition: 'background-color 0.3s',
  },
  buttonHover: {
    backgroundColor: '#0056b3',
  },
};

export default GrievanceForm;
