import React, { useEffect, useState } from 'react';
import { getUserGrievances } from '../services/api';
import { useAuth } from '../context/Authcontext'; // Import the useAuth hook
import './Navbar.css';

const Profile = () => {
  const [grievances, setGrievances] = useState([]);
  const { userEmail } = useAuth(); // Call useAuth inside the component
  const [email, setEmail] = useState(userEmail); // Set email from useAuth

  useEffect(() => {
    if (email) {
      getUserGrievances(email).then(response => {
        setGrievances(response.data);
      }).catch(error => {
        console.error('Error fetching grievances:', error);
      });
    }
  }, [email]);

  return (
    <div className="profile-container">
      <h2>Profile</h2>
      <p>Email: {email}</p>
      <h3>Your Grievances:</h3>
      <table>
        <thead>
          <tr>
            <th>Type</th>
            <th>Title</th>
            <th>Description</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {grievances.map(grievance => (
            <tr key={grievance._id}>
              <td>{grievance.type}</td>
              <td>{grievance.title}</td>
              <td>{grievance.description}</td>
              <td>{grievance.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Profile;
