import React, { useEffect, useState } from 'react';
import { getUserGrievances } from '../services/api';

const Profile = () => {
  const [grievances, setGrievances] = useState([]);
  const email = localStorage.getItem('userEmail'); // Assuming the email is stored in localStorage after login

  useEffect(() => {
    const fetchGrievances = async () => {
      try {
        const response = await getUserGrievances(email);
        setGrievances(response.data);
      } catch (error) {
        console.error('Error fetching grievances:', error);
      }
    };

    fetchGrievances();
  }, [email]);

  return (
    <div className="profile-container">
      <h2>Profile</h2>
      <p>Email: {email}</p>
      <h3>Your Grievances:</h3>
      {grievances.length > 0 ? (
        <ul>
          {grievances.map((grievance) => (
            <li key={grievance._id}>
              <strong>Type:</strong> {grievance.type}<br/>
              <strong>Title:</strong> {grievance.title}<br/>
              <strong>Description:</strong> {grievance.description}<br/>
              <strong>Status:</strong> {grievance.status}
            </li>
          ))}
        </ul>
      ) : (
        <p>No grievances submitted.</p>
      )}
    </div>
  );
};

export default Profile;
