import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';
import { logout } from '../services/api';  // Import the logout function
// import { FaUserCircle } from 'react-icons/fa';  // Commented out
import { FaBars } from 'react-icons/fa';

const Navbar = () => {
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem('token');  // Check if user is logged in
  const [showMenu, setShowMenu] = useState(false);

  const handleLogout = () => {
    logout();  // Call the logout function to clear the token
    navigate('/login');  // Redirect to the login page
  };

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">Grievance Website</Link>
        <div className={`navbar-links ${showMenu ? 'active' : ''}`}>
          <Link to="/" className="nav-item">Home</Link>
          {isAuthenticated ? (
            <>
              <Link to="/grievance" className="nav-item">Submit Grievance</Link>
              <Link to="/admin" className="nav-item">Admin Dashboard</Link>
              <button onClick={handleLogout} className="nav-item logout-button">Logout</button>
            </>
          ) : (
            <>
              <Link to="/login" className="nav-item">Login</Link>
              <Link to="/register" className="nav-item">Register</Link>
            </>
          )}
        </div>
        <div className="menu-icon" onClick={toggleMenu}>
          <FaBars />
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
