import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext'; // Ensure this import is correct
import { submitGrievance } from '../services/api';
import '../pages/Login.css';

const GrievanceForm = () => {
  const { userEmail } = useAuth(); // Call useAuth inside the component
  const [formData, setFormData] = useState({
    type: '',
    title: '',
    description: '',
    document: null,
  });

  const handleChange = (e) => {
    if (e.target.name === 'document') {
      setFormData({
        ...formData,
        document: e.target.files[0],
      });
    } else {
      setFormData({
        ...formData,
        [e.target.name]: e.target.value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await submitGrievance({
        ...formData,
        email: userEmail, // Use the logged-in user's email
      });
      alert('Grievance submitted successfully!');
    } catch (error) {
      alert('Error submitting grievance');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="email" value={userEmail} disabled required />
      <input type="text" name="type" placeholder="Type" onChange={handleChange} required />
      <input type="text" name="title" placeholder="Title" onChange={handleChange} required />
      <textarea name="description" placeholder="Description" onChange={handleChange} required />
      <input type="file" name="document" onChange={handleChange} />
      <button type="submit">Submit Grievance</button>
    </form>
  );
};

export default GrievanceForm;
