import React, { useState, useEffect } from 'react';
import { getGrievances, updateGrievanceStatus } from '../services/api'; // Ensure this import is correct
import { Table, Container, Row, Col, Spinner, Button } from 'react-bootstrap';

const AdminDashboard = () => {
  const [grievances, setGrievances] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchGrievances();
  }, []);

  const fetchGrievances = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await getGrievances(token);
      setGrievances(response.data);
    } catch (error) {
      alert('Error fetching grievances');
    } finally {
      setLoading(false);
    }
  };

  const handleResolve = async (id) => {
    try {
      await updateGrievanceStatus(id, 'Resolved'); // Mark as Resolved
      setGrievances(grievances.map(g => g._id === id ? { ...g, status: 'Resolved' } : g)); // Update state
      alert('Grievance marked as resolved');
    } catch (error) {
      alert('Error updating status');
    }
  };

  if (loading) {
    return (
      <Container className="mt-5 text-center">
        <Spinner animation="border" />
      </Container>
    );
  }

  return (
    <Container className="mt-5">
      <Row>
        <Col>
          <h2 className="text-center mb-4">Admin Dashboard</h2>
          <Table striped bordered hover responsive>
            <thead>
              <tr>
                <th>Email</th>
                <th>Type</th>
                <th>Title</th>
                <th>Description</th>
                <th>Status</th>
                <th>Date Submitted</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {grievances.map((grievance) => (
                <tr key={grievance._id}>
                  <td>{grievance.email}</td>
                  <td>{grievance.type}</td>
                  <td>{grievance.title}</td>
                  <td>{grievance.description}</td>
                  <td>{grievance.status}</td>
                  <td>{new Date(grievance.createdAt).toLocaleString()}</td>
                  <td>
                    {grievance.status !== 'Resolved' && (
                      <Button variant="success" onClick={() => handleResolve(grievance._id)}>
                        Mark as Resolved
                      </Button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Col>
      </Row>
    </Container>
  );
};

export default AdminDashboard;
