import React, { useState } from 'react';
import { submitGrievance } from '../services/api';
import '../pages/Login.css';

const GrievanceForm = () => {
  const [formData, setFormData] = useState({
    email: '',  // This will be set from the logged-in user's info
    type: '',
    title: '',
    description: '',
    document: null, // For file upload
  });

  const [message, setMessage] = useState(''); // For storing success or error messages

  // Simulate getting logged-in user's email from context or local storage
  useEffect(() => {
    const userEmail = localStorage.getItem('userEmail'); // Assume the email is stored in localStorage upon login
    setFormData(formData => ({ ...formData, email: userEmail }));
  }, []);

  const handleChange = (e) => {
    if (e.target.name === 'document') {
      setFormData({
        ...formData,
        document: e.target.files[0],
      });
    } else {
      setFormData({
        ...formData,
        [e.target.name]: e.target.value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await submitGrievance(formData);
      setMessage('Grievance submitted successfully!');
      setFormData({ email: formData.email, type: '', title: '', description: '', document: null });
    } catch (error) {
      setMessage(`Error submitting grievance: ${error.response?.data?.message || 'Server error'}`);
    }
  };

  return (
    <div className="grievance-form-container">
      <form onSubmit={handleSubmit}>
        <label>Email</label>
        <input 
          type="email" 
          name="email" 
          value={formData.email}
          readOnly  // Make email field read-only
        />

          <label>Type</label>
          <input 
            type="text" 
            name="type" 
            placeholder="Grievance type"
            value={formData.type}
            onChange={handleChange}
            required
          />

          <label>Title</label>
          <input 
            type="text" 
            name="title" 
            placeholder="Title"
            value={formData.title}
            onChange={handleChange}
            required
          />

          <label>Description</label>
          <textarea 
            name="description" 
            placeholder="Description"
            value={formData.description}
            onChange={handleChange}
            required
          />

          <label>Upload Supporting Document</label>
          <input 
            type="file" 
            name="document"
            onChange={handleChange}
          />

          <button className="grievance-btn" type="submit">Submit Grievance</button>
        </form>

        {message && <p>{message}</p>} {/* Display the message */}
      </div>
  
  );
};

export default GrievanceForm;
