import React, { useEffect, useState } from 'react';
import GrievanceForm from '../components/GrievanceForm';
import GrievanceList from '../components/GrievanceList';

import axios from 'axios';

const Grievance = () => {
  const [grievances, setGrievances] = useState([]);

  useEffect(() => {
    const fetchGrievances = async () => {
      try {
        const response = await axios.get('/api/grievances', {
          headers: {
            'x-auth-token': localStorage.getItem('token')
          }
        });
        setGrievances(response.data);
      } catch (error) {
        console.error('Error fetching grievances:', error);
      }
    };

    fetchGrievances();
  }, []);

  const addGrievance = async (grievanceData) => {
    try {
      const response = await axios.post('/api/grievances/submit', grievanceData, {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      setGrievances([response.data, ...grievances]);
    } catch (error) {
      console.error('Error adding grievance:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h1>Submit Your Grievance</h1>
      <GrievanceForm onSubmit={addGrievance} />
      <GrievanceList grievances={grievances} />
    </div>
  );
};

export default Grievance;
