import React, { useEffect, useState } from 'react';
import { getUserGrievances } from '../services/api';

const Profile = () => {
  const [grievances, setGrievances] = useState([]);
  const [email, setEmail] = useState('');

  useEffect(() => {
    const email = localStorage.getItem('email'); // Or however you store the email
    setEmail(email);
    
    getUserGrievances().then(response => {
      setGrievances(response.data);
    }).catch(error => {
      console.error('Error fetching grievances:', error);
    });
  }, []);

  return (
    <div className="profile-page">
      <h1>Profile</h1>
      <p>Email: {email}</p>
      <h2>Your Grievances:</h2>
      {grievances.length === 0 ? (
        <p>No grievances submitted.</p>
      ) : (
        <ul>
          {grievances.map(grievance => (
            <li key={grievance._id}>
              <h3>{grievance.title}</h3>
              <p>{grievance.description}</p>
              <p>Status: {grievance.status}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Profile;
