import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';
import { logout } from '../services/api';
import { FaUserCircle, FaSearch, FaBars } from 'react-icons/fa';

const Navbar = () => {
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem('token');
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const [isNavOpen, setNavOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const toggleDropdown = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  const toggleNav = () => {
    setNavOpen(!isNavOpen);
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">Grievance Website</Link>
        <div className={`navbar-links ${isNavOpen ? 'active' : ''}`}>
          <Link to="/" className="nav-item">Home</Link>
          {isAuthenticated ? (
            <>
              <Link to="/grievance" className="nav-item">Submit Grievance</Link>
              <Link to="/admin" className="nav-item">Admin Dashboard</Link>
              <div className="dropdown">
                <button className="nav-item profile-button" onClick={toggleDropdown}>
                  <FaUserCircle size={24} />
                </button>
                {isDropdownOpen && (
                  <div className="dropdown-content">
                    <Link to="/profile" className="dropdown-item">Profile</Link>
                    <button className="dropdown-item logout-button" onClick={handleLogout}>
                      Logout
                    </button>
                  </div>
                )}
              </div>
            </>
          ) : (
            <>
              <Link to="/login" className="nav-item">Login</Link>
              <Link to="/register" className="nav-item">Register</Link>
            </>
          )}
          <div className="search-bar">
            <input type="text" placeholder="Search..." />
            <FaSearch className="search-icon" />
          </div>
        </div>
        <button className="menu-toggle" onClick={toggleNav}>
          <FaBars size={24} />
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
