import React, { useState } from 'react';
import { submitGrievance } from '../services/api';
import '../pages/Login.css';  // Corrected path to Login.css

const GrievanceForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    type: '',
    title: '',
    description: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    submitGrievance(formData, token)
      .then((response) => {
        alert('Grievance submitted successfully');
      })
      .catch((error) => {
        console.error('Error submitting grievance:', error);
        alert(`Error submitting grievance: ${error.response?.data?.message || 'Server error'}`);
      });
  };

  return (
    <div className="grievance-form-container">
      <div className="grievance-form">
        <h2>Submit Grievance</h2>
        <form onSubmit={handleSubmit}>
          <label>Email</label>
          <input 
            type="email" 
            name="email" 
            placeholder="Email" 
            value={formData.email}
            onChange={handleChange} 
            required 
          />

          <label>Type</label>
          <input 
            type="text" 
            name="type"
            placeholder="Type" 
            value={formData.type}
            onChange={handleChange} 
            required 
          />

          <label>Title</label>
          <input 
            type="text" 
            name="title"
            placeholder="Title" 
            value={formData.title}
            onChange={handleChange} 
            required 
          />

          <label>Description</label>
          <textarea 
            name="description" 
            placeholder="Description" 
            value={formData.description}
            onChange={handleChange} 
            required 
          />

          <button className="grievance-btn">Submit Grievance</button>
        </form>
      </div>
    </div>
  );
};

export default GrievanceForm;
