import React, { useState } from 'react';
import { submitGrievance } from '../services/api';
import './GrievanceForm.css';

const GrievanceForm = () => {
  const [formData, setFormData] = useState({
    user_id: '',
    type: '',
    title: '',
    description: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      await submitGrievance(formData, token);
      alert('Grievance submitted successfully');
    } catch (error) {
      console.error('Error submitting grievance:', error);
      alert(`Error submitting grievance: ${error.response?.data?.message || 'Server error'}`);
    }
  };

  return (
    <div className="grievance-container">
      <form onSubmit={handleSubmit} className="grievance-form">
        <div className="form-group">
          <label>User ID</label>
          <input type="text" name="user_id" className="form-control" placeholder="User ID" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Type</label>
          <input type="text" name="type" className="form-control" placeholder="Type" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Title</label>
          <input type="text" name="title" className="form-control" placeholder="Title" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Description</label>
          <textarea name="description" className="form-control" placeholder="Description" onChange={handleChange} required></textarea>
        </div>
        <button type="submit" className="btn btn-primary btn-block mt-4">Submit Grievance</button>
      </form>
    </div>
  );
};

export default GrievanceForm;
