import React, { useState } from 'react';
import { FaSearch } from 'react-icons/fa';
import './Navbar.css';
import { searchSite } from '../services/api';

const Navbar = () => {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState({ grievances: [], users: [], helpArticles: [] });

    const handleSearch = async (e) => {
        e.preventDefault();
        if (query.trim()) {
            const searchResults = await searchSite(query);
            setResults(searchResults);
        }
    };

    return (
        <nav className="navbar">
            <div className="navbar-container">
                <a href="/" className="navbar-logo">Grievance Website</a>
                <div className="navbar-links">
                    <a href="/" className="nav-item">Home</a>
                    <a href="/login" className="nav-item">Login</a>
                    <a href="/register" className="nav-item">Register</a>
                    <form onSubmit={handleSearch} className="search-form">
                        <input
                            type="text"
                            value={query}
                            onChange={(e) => setQuery(e.target.value)}
                            placeholder="Search..."
                            className="search-input"
                        />
                        <button type="submit" className="search-button"><FaSearch /></button>
                    </form>
                </div>
            </div>

            {/* Results Dropdown */}
            {query && (
                <div className="search-results">
                    {results.grievances.length > 0 && (
                        <div>
                            <h4>Grievances</h4>
                            <ul>
                                {results.grievances.map(grievance => (
                                    <li key={grievance._id}>{grievance.title}</li>
                                ))}
                            </ul>
                        </div>
                    )}
                    {results.users.length > 0 && (
                        <div>
                            <h4>Users</h4>
                            <ul>
                                {results.users.map(user => (
                                    <li key={user._id}>{user.name} - {user.email}</li>
                                ))}
                            </ul>
                        </div>
                    )}
                    {results.helpArticles.length > 0 && (
                        <div>
                            <h4>Help Articles</h4>
                            <ul>
                                {results.helpArticles.map(article => (
                                    <li key={article._id}>{article.title}</li>
                                ))}
                            </ul>
                        </div>
                    )}
                    {results.grievances.length === 0 && results.users.length === 0 && results.helpArticles.length === 0 && (
                        <div className="no-results">No results found</div>
                    )}
                </div>
            )}
        </nav>
    );
};

export default Navbar;
