import React, { useState } from 'react';
import { submitGrievance } from '../services/api';
import '../pages/Login.css';  // Corrected path to Login.css

const GrievanceForm = () => {
  const [formData, setFormData] = useState({
    user_id: '',
    type: '',
    title: '',
    description: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    submitGrievance(formData, token)
      .then((response) => {
        alert('Grievance submitted successfully');
      })
      .catch((error) => {
        console.error('Error submitting grievance:', error);
        alert(`Error submitting grievance: ${error.response?.data?.message || 'Server error'}`);
      });
  };

  return (
    <div className="grievance-form-container">
      <form onSubmit={handleSubmit} className="grievance-form">
        <h2 className="text-center">Submit Grievance</h2>
        <div className="form-group">
          <label>User ID</label>
          <input
            type="text"
            name="user_id"
            className="form-control"
            placeholder="User ID"
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Type</label>
          <input
            type="text"
            name="type"
            className="form-control"
            placeholder="Type"
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            name="title"
            className="form-control"
            placeholder="Title"
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Description</label>
          <textarea
            name="description"
            className="form-control"
            placeholder="Description"
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Submit Grievance</button>
      </form>
    </div>
  );
};

export default GrievanceForm;
