import React, { useState, useEffect } from 'react';
import { Fade } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import './Navbar';
import { useNavigate } from 'react-router-dom';

const IntroSlideshow = () => {
  const navigate = useNavigate();
  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    // Automatically navigate to home after the slideshow ends
    const timer = setTimeout(() => {
      navigate('/home'); // Adjust the path as needed
    }, 5000); // Adjust the timing as needed

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="slideshow-container">
      <Fade autoplay={true} arrows={false} onChange={(previous, next) => setCurrentSlide(next)}>
        <div className="each-slide">
          <h1 className={`typing ${currentSlide === 0 ? 'typewriter' : ''}`}>Hi</h1>
        </div>
        <div className="each-slide">
          <h1 className={`typing ${currentSlide === 1 ? 'typewriter' : ''}`}>Welcome</h1>
        </div>
      </Fade>
    </div>
  );
};

export default IntroSlideshow;
