import React, { useState } from 'react';
import { submitGrievance } from '../services/api';
import '../pages/Login.css';

const GrievanceForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    type: '',
    title: '',
    description: '',
    document: null,
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      document: e.target.files[0], // Get the selected file
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('email', formData.email);
    data.append('type', formData.type);
    data.append('title', formData.title);
    data.append('description', formData.description);
    if (formData.document) {
      data.append('document', formData.document);
    }

    submitGrievance(data)
      .then((response) => {
        alert('Grievance submitted successfully');
      })
      .catch((error) => {
        console.error('Error submitting grievance:', error);
        alert(`Error submitting grievance: ${error.response?.data?.message || 'Server error'}`);
      });
  };

  return (
    <div className="grievance-form-container">
      <div className="grievance-form">
        <h2>Submit Grievance</h2>
        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <label>Email</label>
          <input
            type="email"
            name="email"
            placeholder="Your email"
            value={formData.email}
            onChange={handleChange}
            required
          />

          <label>Type</label>
          <input
            type="text"
            name="type"
            placeholder="Grievance Type"
            value={formData.type}
            onChange={handleChange}
            required
          />

          <label>Title</label>
          <input
            type="text"
            name="title"
            placeholder="Title"
            value={formData.title}
            onChange={handleChange}
            required
          />

          <label>Description</label>
          <textarea
            name="description"
            placeholder="Description"
            value={formData.description}
            onChange={handleChange}
            required
          />

          <label>Upload Supporting Document</label>
          <input type="file" name="document" onChange={handleFileChange} />

          <button className="grievance-btn">Submit Grievance</button>
        </form>
      </div>
    </div>
  );
};

export default GrievanceForm;
