import React, { useState } from 'react';
import { submitGrievance } from '../services/api';
import '../pages/Login.css';  // Assuming you're using this CSS file

const GrievanceForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    type: '',
    title: '',
    description: '',
    document: null,
  });

  const handleChange = (e) => {
    if (e.target.name === 'document') {
      setFormData({
        ...formData,
        [e.target.name]: e.target.files[0] // Handle file input
      });
    } else {
      setFormData({
        ...formData,
        [e.target.name]: e.target.value
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const data = new FormData();
    Object.keys(formData).forEach(key => {
      data.append(key, formData[key]);
    });

    submitGrievance(data, token)
      .then((response) => {
        alert('Grievance submitted successfully');
      })
      .catch((error) => {
        console.error('Error submitting grievance:', error);
        alert(`Error submitting grievance: ${error.response?.data?.message || 'Server error'}`);
      });
  };

  return (
    <div className="grievance-form-container">
      <div className="grievance-form">
        <h2>Submit Grievance</h2>
        <form onSubmit={handleSubmit}>
          <label>Email</label>
          <input 
            type="email" 
            name="email" 
            placeholder="Your Email" 
            value={formData.email}
            onChange={handleChange} 
            required 
          />

          <label>Type</label>
          <input 
            type="text" 
            name="type"
            placeholder="Type" 
            value={formData.type}
            onChange={handleChange} 
            required 
          />

          <label>Title</label>
          <input 
            type="text" 
            name="title"
            placeholder="Title" 
            value={formData.title}
            onChange={handleChange} 
            required 
          />

          <label>Description</label>
          <textarea 
            name="description" 
            placeholder="Description" 
            value={formData.description}
            onChange={handleChange} 
            required 
          />

          <label>Upload Supporting Document</label>
          <input 
            type="file" 
            name="document"
            onChange={handleChange} 
          />

          <button className="grievance-btn">Submit Grievance</button>
        </form>
      </div>
    </div>
  );
};

export default GrievanceForm;
