import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL;

export const register = async (data) => {
  return await axios.post(`${API_URL}/auth/register`, data);
};

export const login = async (data) => {
  return await axios.post(`${API_URL}/auth/login`, data);
};

// src/services/api.js


export const submitGrievance = async (formData) => {
  const token = localStorage.getItem('token');
  return await axios.post('http://localhost:5000/api/grievances', formData, {
    headers: {
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'multipart/form-data',
    },
  });
};


export const getGrievances = async (token) => {
  return await axios.get(`${API_URL}/grievances`, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });
};

export const updateGrievanceStatus = async (id, status) => {
  return await axios.put(`${process.env.REACT_APP_API_URL}/grievances/update-status/${id}`, { status });
};


// New logout function
export const logout = () => {
  localStorage.removeItem('token');
};
export const searchSite = async (query) => {
  const response = await fetch(`/api/search?q=${query}`);
  if (!response.ok) throw new Error('Failed to fetch search results');
  return await response.json();
};

// In api.js or wherever you handle API requests

export const getUserGrievances = async (email) => {
  return await axios.get('/api/grievances/user-grievances', {
    params: { email }
  });
};
