import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    // Initialize Google Sign-In
    window.google.accounts.id.initialize({
      client_id: 'YOUR_GOOGLE_CLIENT_ID', // Replace with your Google Client ID
      callback: handleGoogleSignIn,
    });

    window.google.accounts.id.renderButton(
      document.getElementById('google-signin-button'),
      { theme: 'outline', size: 'large' }
    );

    // Initialize Facebook SDK
    window.fbAsyncInit = function() {
      window.FB.init({
        appId: 'YOUR_FACEBOOK_APP_ID', // Replace with your Facebook App ID
        cookie: true,
        xfbml: true,
        version: 'v16.0' // Use the latest version of the Facebook API
      });
    };

  }, []);

  const handleGoogleSignIn = (response) => {
    console.log('Google user ID token:', response.credential);
    alert('Google Sign-In successful');
    navigate('/grievance');
  };

  const handleFacebookSignIn = () => {
    window.FB.login(function(response) {
      if (response.authResponse) {
        console.log('Facebook Access Token:', response.authResponse.accessToken);
        alert('Facebook Sign-In successful');
        navigate('/grievance');
      } else {
        alert('Facebook Sign-In failed');
      }
    }, {scope: 'public_profile,email'});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const loginSuccess = true;

    if (loginSuccess) {
      alert('Login successful');
      navigate('/grievance');
    } else {
      alert('Login failed');
    }
  };

  return (
    <>
      <Navbar />
      <div className="login-container">
        <div className="login-card">
          <h2>Login</h2>
          <p>Don’t have an account yet? <a href="/register">Sign Up</a></p>
          <form onSubmit={handleSubmit}>
            <label>Email Address</label>
            <input 
              type="email" 
              placeholder="you@example.com" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />

            <label>Password</label>
            <input 
              type="password" 
              placeholder="Enter 6 characters or more" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />

            <div className="login-options">
              <label>
                <input type="checkbox" /> Remember me
              </label>
              <a href="/forgot-password" className="forgot-password">Forgot Password?</a>
            </div>

            <button className="login-btn">LOGIN</button>

            <div className="social-login">
              <p>or login with</p>
              <div className="social-buttons">
                <div id="google-signin-button"></div> {/* Google Sign-In button */}
                <button className="facebook-btn" onClick={handleFacebookSignIn}>
                  <img src="/facebook.jpg" alt="Facebook" /> Facebook
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default Login;
