import React, { useState, useEffect } from 'react';
import { submitGrievance } from '../services/api';
import { useAuth } from '../context/AuthContext'; // Import useAuth
import '../pages/Login.css';



const GrievanceForm = () => {
  const { userEmail } = useAuth(); // Get userEmail from useAuth
  const [formData, setFormData] = useState({
    email: userEmail || '', // Initialize email with userEmail, or empty string if undefined
    type: '',
    title: '',
    description: '',
    document: null, // For file upload
  });

  const [message, setMessage] = useState(''); // For storing success or error messages

  const handleChange = (e) => {
    if (e.target.name === 'document') {
      setFormData({
        ...formData,
        document: e.target.files[0],
      });
    } else {
      setFormData({
        ...formData,
        [e.target.name]: e.target.value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await submitGrievance(formData);
      setMessage('Grievance submitted successfully!');
      // Optionally, clear the form after submission
      setFormData({ email: userEmail, type: '', title: '', description: '', document: null });
    } catch (error) {
      setMessage(`Error submitting grievance: ${error.response?.data?.message || 'Server error'}`);
    }
  };

  return (
    <div className="grievance-form-container">
      <div className="grievance-form">
        <h2>Submit Grievance</h2>
        <form onSubmit={handleSubmit}>
          <label>Email</label>
          <input 
            type="email" 
            name="email" 
            placeholder="Your email"
            value={formData.email}
            disabled // Disable the email field to prevent editing
            required
          />

          <label>Type</label>
          <input 
            type="text" 
            name="type" 
            placeholder="Grievance type"
            value={formData.type}
            onChange={handleChange}
            required
          />

          <label>Title</label>
          <input 
            type="text" 
            name="title" 
            placeholder="Title"
            value={formData.title}
            onChange={handleChange}
            required
          />

          <label>Description</label>
          <textarea 
            name="description" 
            placeholder="Description"
            value={formData.description}
            onChange={handleChange}
            required
          />

          <label>Upload Supporting Document</label>
          <input 
            type="file" 
            name="document"
            onChange={handleChange}
          />

          <button className="grievance-btn" type="submit">Submit Grievance</button>
        </form>

        {message && <p>{message}</p>} {/* Display the message */}
      </div>
    </div>
  );
};

export default GrievanceForm;
