import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import { useHistory } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const history = useHistory();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Perform your login logic here
    const loginSuccess = true; // Replace with your actual login success condition

    if (loginSuccess) {
      alert('Login successful');
      history.push('/grievance'); // Redirect to Submit Grievance page
    } else {
      alert('Login failed');
    }
  };

  return (
    <>
      <Navbar />
      <div className="login-container">
        <div className="login-card">
          <h2>Login</h2>
          <p>Donâ€™t have an account yet? <a href="/register">Sign Up</a></p>
          <form onSubmit={handleSubmit}>
            <label>Email Address</label>
            <input 
              type="email" 
              placeholder="you@example.com" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />

            <label>Password</label>
            <input 
              type="password" 
              placeholder="Enter 6 characters or more" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />

            <div className="login-options">
              <label>
                <input type="checkbox" /> Remember me
              </label>
              <a href="/forgot-password" className="forgot-password">Forgot Password?</a>
            </div>

            <button className="login-btn">LOGIN</button>

            <div className="social-login">
              <p>or login with</p>
              <div className="social-buttons">
                <button className="google-btn" onClick={() => window.location.href = 'https://accounts.google.com/'}>
                  <img src="/google.png" alt="Google" /> Google
                </button>
                <button className="facebook-btn" onClick={() => window.location.href = 'https://www.facebook.com/'}>
                  <img src="/facebook.jpg" alt="Facebook" /> Facebook
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default Login;
