import React, { useEffect, useState } from 'react';
import { getUserGrievances } from '../services/api';
import { useAuth } from '../context/AuthContext'; // Ensure this is the correct import
import './Navbar.css';

const Profile = () => {
  const [grievances, setGrievances] = useState([]);
  const { userEmail } = useAuth(); // Get the logged-in user's email from context

  useEffect(() => {
    console.log('Fetching grievances for email:', userEmail); // Log the email
    if (userEmail) {
      getUserGrievances(userEmail).then(response => {
        console.log('Fetched grievances:', response.data); // Log fetched grievances
        setGrievances(response.data);
      }).catch(error => {
        console.error('Error fetching grievances:', error);
      });
    }
  }, [userEmail]);
  

  return (
    <div className="profile-container">
      <h2>Profile</h2>
      <p>Email: {userEmail}</p> {/* Display the logged-in user's email */}
      <h3>Your Grievances:</h3>
      <table>
        <thead>
          <tr>
            <th>Type</th>
            <th>Title</th>
            <th>Description</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {grievances.length > 0 ? (
            grievances.map(grievance => (
              <tr key={grievance._id}>
                <td>{grievance.type}</td>
                <td>{grievance.title}</td>
                <td>{grievance.description}</td>
                <td>{grievance.status}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No grievances submitted yet.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Profile;
