import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { login } from '../services/api'; // Assuming you have a login API function
import './Login.css';

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [loginError, setLoginError] = useState('');
  const [loginSuccess, setLoginSuccess] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await login(formData);
      setLoginSuccess(true);
      setTimeout(() => navigate('/dashboard'), 2000); // Redirect to dashboard or home after 2 seconds
    } catch (error) {
      setLoginError('Invalid email or password. Please try again.');
    }
  };

  const handleGoogleLogin = () => {
    window.location.href = 'https://accounts.google.com/'; // Redirect to Google login
  };

  const handleFacebookLogin = () => {
    window.location.href = 'https://www.facebook.com/'; // Redirect to Facebook login
  };

  return (
    <>
      <Navbar />
      <div className="login-container">
        <div className="login-card">
          <h2>Login</h2>
          <p>Don’t have an account yet? <a href="/register">Sign Up</a></p>
          <form onSubmit={handleSubmit}>
            <label>Email Address</label>
            <input
              type="email"
              name="email"
              placeholder="you@example.com"
              value={formData.email}
              onChange={handleChange}
              required
            />

            <label>Password</label>
            <input
              type="password"
              name="password"
              placeholder="Enter 6 characters or more"
              value={formData.password}
              onChange={handleChange}
              required
            />

            <div className="login-options">
              <label>
                <input type="checkbox" /> Remember me
              </label>
              <a href="/forgot-password" className="forgot-password">Forgot Password?</a>
            </div>

            {loginError && <p style={{ color: 'red' }}>{loginError}</p>}
            {loginSuccess && <p style={{ color: 'green' }}>Login successful! Redirecting...</p>}

            <button className="login-btn">LOGIN</button>

            <div className="social-login">
              <p>or login with</p>
              <div className="social-buttons">
                <button type="button" className="google-btn" onClick={handleGoogleLogin}>
                  <img src="/google.png" alt="Google" /> Google
                </button>
                <button type="button" className="facebook-btn" onClick={handleFacebookLogin}>
                  <img src="/facebook.jpg" alt="Facebook" /> Facebook
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default Login;
