import React, { useState } from 'react';
import { submitGrievance } from '../services/api';
import '../pages/Login.css';

const GrievanceForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    type: '',
    title: '',
    description: '',
    document: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      document: e.target.files[0],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    const formDataToSend = new FormData();
    Object.keys(formData).forEach(key => {
      formDataToSend.append(key, formData[key]);
    });

    try {
      await submitGrievance(formDataToSend, token);
      alert('Grievance submitted successfully');
    } catch (error) {
      console.error('Error submitting grievance:', error);
      alert(`Error submitting grievance: ${error.response?.data?.message || 'Server error'}`);
    }
  };

  return (
    <div className="grievance-form-container">
      <div className="grievance-form">
        <h2>Submit Grievance</h2>
        <form onSubmit={handleSubmit}>
          <label>Email</label>
          <input 
            type="email" 
            name="email" 
            placeholder="Your Email" 
            value={formData.email}
            onChange={handleChange} 
            required 
          />

          <label>Type</label>
          <input 
            type="text" 
            name="type"
            placeholder="Type" 
            value={formData.type}
            onChange={handleChange} 
            required 
          />

          <label>Title</label>
          <input 
            type="text" 
            name="title"
            placeholder="Title" 
            value={formData.title}
            onChange={handleChange} 
            required 
          />

          <label>Description</label>
          <textarea 
            name="description" 
            placeholder="Description" 
            value={formData.description}
            onChange={handleChange} 
            required 
          />

          <label>Supporting Document</label>
          <input 
            type="file" 
            name="document"
            onChange={handleFileChange} 
          />

          <button className="grievance-btn">Submit Grievance</button>
        </form>
      </div>
    </div>
  );
};

export default GrievanceForm;
