import React, { useState, useEffect } from 'react';
import { submitGrievance } from '../services/api';
import '../pages/Login.css';
import { useAuth } from '../context/AuthContext';

const GrievanceForm = () => {
  const { userEmail } = useAuth();  // Extract the email from your context

  const [formData, setFormData] = useState({
    email: userEmail || '',  // Set the initial email to the logged-in user's email
    type: '',
    title: '',
    description: '',
    document: null,
  });

  const [message, setMessage] = useState('');

  useEffect(() => {
    if (userEmail) {
      setFormData((prevData) => ({
        ...prevData,
        email: userEmail,
      }));
    }
  }, [userEmail]);

  const handleChange = (e) => {
    if (e.target.name === 'document') {
      setFormData({
        ...formData,
        document: e.target.files[0],
      });
    } else {
      setFormData({
        ...formData,
        [e.target.name]: e.target.value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await submitGrievance(formData);
      setMessage('Grievance submitted successfully!');
      setFormData({ email: userEmail, type: '', title: '', description: '', document: null });
    } catch (error) {
      setMessage(`Error submitting grievance: ${error.response?.data?.message || 'Server error'}`);
    }
  };

  return (
    <div className="grievance-form-container">
      <div className="grievance-form">
        <h2>Submit Grievance</h2>
        <form onSubmit={handleSubmit}>
          <label>Type</label>
          <input 
            type="text" 
            name="type" 
            placeholder="Grievance type"
            value={formData.type}
            onChange={handleChange}
            required
          />

          <label>Title</label>
          <input 
            type="text" 
            name="title" 
            placeholder="Title"
            value={formData.title}
            onChange={handleChange}
            required
          />

          <label>Description</label>
          <textarea 
            name="description" 
            placeholder="Description"
            value={formData.description}
            onChange={handleChange}
            required
          />

          <label>Upload Supporting Document</label>
          <input 
            type="file" 
            name="document"
            onChange={handleChange}
          />

          <button className="grievance-btn" type="submit">Submit Grievance</button>
        </form>

        {message && <p>{message}</p>}
      </div>
    </div>
  );
};

export default GrievanceForm;
