import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaSearch, FaUserCircle } from 'react-icons/fa';
import './Navbar.css';
import { logout } from '../services/api'; // Import the logout function

const Navbar = () => {
    const navigate = useNavigate();
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [query, setQuery] = useState(''); // Add state for the search query

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsAuthenticated(true);
        }
    }, []);
    

    const handleLogout = () => {
        logout(); // Call the logout function to clear the token
        localStorage.removeItem('token'); // Clear token from localStorage
        setIsAuthenticated(false); // Update state to reflect logout
        navigate('/login'); // Redirect to the login page
    };

    const handleSearch = (e) => {
        e.preventDefault();
        if (query.trim() !== '') {
            // Redirect to Google Scholar with the search query
            window.open(`https://scholar.google.co.in/scholar?q=${encodeURIComponent(query)}&hl=en&as_sdt=0&as_vis=1&oi=scholart`, '_blank');
        }
    };

    return (
        <nav className="navbar">
            <div className="navbar-container">
                <Link to="/" className="navbar-logo">Grievance Website</Link>
                <div className="navbar-links">
                    <Link to="/" className="nav-item">Home</Link>
                    {isAuthenticated && (
                        <>
                            <Link to="/grievance" className="nav-item">Submit Grievance</Link>
                            <Link to="/admin" className="nav-item">Admin Dashboard</Link>
                        </>
                    )}
                    <form onSubmit={handleSearch} className="search-form">
                        <input
                            type="text"
                            placeholder="Search..."
                            className="search-input"
                            value={query} // Bind the input to query state
                            onChange={(e) => setQuery(e.target.value)} // Update query state on input change
                        />
                        <button type="submit" className="search-button"><FaSearch /></button>
                    </form>
                    {isAuthenticated && (
                        <div className="dropdown">
                            <button className="nav-item profile-button">
                                <FaUserCircle size={20} />
                            </button>
                            <div className="dropdown-menu">
                            <Link to="/profile" className="nav-itemss">Profile</Link>
                                <button onClick={handleLogout} className="dropdown-item logout-button">Logout</button>
                            </div>
                        </div>
                    )}
                    {!isAuthenticated && (
                        <>
                            <Link to="/login" className="nav-item">Login</Link>
                            <Link to="/register" className="nav-item">Register</Link>
                        </>
                    )}
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
