import React from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import './Home.css';

const Home = () => {
  const backgroundImageStyle = {
    backgroundImage: "url('/comp.jpeg')",
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    height: '100vh',
    width: '100vw',  // Ensure it takes the full width
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',  // Center content vertically
    alignItems: 'flex-end',    // Align content to the right
    padding: '0 10% 0 0',  // Add padding on the right to balance the layout
    paddingTop: '4rem'  // Adjust for fixed navbar height
  };

  return (
    <div style={backgroundImageStyle}>
      <Navbar />
      <div className="content">
        <h1 className="welcome-text">Welcome to Grievance Website!</h1>
      </div>
      <Footer />
    </div>
  );
};
