import React, { useEffect, useState } from 'react';
import { getGrievances } from '../services/api';
import '/Navbar.css';

const Profile = () => {
  const [grievances, setGrievances] = useState([]);
  const [email, setEmail] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    getGrievances(token).then((response) => {
      setGrievances(response.data);
      setEmail(response.data[0]?.email || ''); // Assuming the email is same for all grievances
    }).catch((error) => {
      console.error('Error fetching grievances:', error);
    });
  }, []);

  return (
    <div className="profile-container">
      <h2>Profile</h2>
      <p>Email: {email}</p>
      <h3>Your Grievances:</h3>
      {grievances.length > 0 ? (
        grievances.map((grievance) => (
          <div key={grievance._id} className="grievance-item">
            <p><strong>Type:</strong> {grievance.type}</p>
            <p><strong>Title:</strong> {grievance.title}</p>
            <p><strong>Description:</strong> {grievance.description}</p>
            <p><strong>Status:</strong> {grievance.status}</p>
          </div>
        ))
      ) : (
        <p>No grievances submitted.</p>
      )}
    </div>
  );
};

export default Profile;
