import React, { useState } from 'react';
import { submitGrievance } from '../services/api';

const GrievanceForm = () => {
  const [formData, setFormData] = useState({
    user_id: '',
    type: '',
    description: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    submitGrievance(formData, token).then((response) => {
      alert('Grievance submitted successfully');
    }).catch((error) => {
      alert('Error submitting grievance');
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="user_id" placeholder="User ID" onChange={handleChange} />
      <input type="text" name="type" placeholder="Type" onChange={handleChange} />
      <textarea name="description" placeholder="Description" onChange={handleChange}></textarea>
      <button type="submit">Submit Grievance</button>
    </form>
  );
};

export default GrievanceForm;
