import React, { useState, useEffect } from 'react';
import { submitGrievance } from '../services/api';

const GrievanceForm = () => {
  const [formData, setFormData] = useState({
    type: '',
    title: '',
    description: '',
    document: null,
  });

  const [message, setMessage] = useState('');

  useEffect(() => {
  const email = localStorage.getItem('email');
  setFormData((prevFormData) => ({
    ...prevFormData,
    email,
  }));
}, []);


  const handleChange = (e) => {
    if (e.target.name === 'document') {
      setFormData({
        ...formData,
        document: e.target.files[0],
      });
    } else {
      setFormData({
        ...formData,
        [e.target.name]: e.target.value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await submitGrievance(formData);
      setMessage('Grievance submitted successfully!');
      // Optionally, clear the form after submission
      setFormData({ type: '', title: '', description: '', document: null });
    } catch (error) {
      setMessage(`Error submitting grievance: ${error.response?.data?.message || 'Server error'}`);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Type</label>
      <input 
        type="text" 
        name="type" 
        placeholder="Grievance type"
        value={formData.type}
        onChange={handleChange}
        required
      />

      <label>Title</label>
      <input 
        type="text" 
        name="title" 
        placeholder="Title"
        value={formData.title}
        onChange={handleChange}
        required
      />

      <label>Description</label>
      <textarea 
        name="description" 
        placeholder="Description"
        value={formData.description}
        onChange={handleChange}
        required
      />

      <label>Upload Supporting Document</label>
      <input 
        type="file" 
        name="document"
        onChange={handleChange}
      />

      <button type="submit">Submit Grievance</button>

      {message && <p>{message}</p>}
    </form>
  );
};

export default GrievanceForm;
