import React, { useState, useEffect } from 'react';
import { getGrievances } from '../services/api';
import { Table, Container, Row, Col, Spinner, Badge } from 'react-bootstrap';

const AdminDashboard = () => {
  const [grievances, setGrievances] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchGrievances();
  }, []);

  const fetchGrievances = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await getGrievances(token);
      setGrievances(response.data);
    } catch (error) {
      alert('Error fetching grievances');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Container className="mt-5 text-center">
        <Spinner animation="border" />
      </Container>
    );
  }

  return (
    <Container className="mt-5">
      <Row>
        <Col>
          <h2 className="text-center mb-4">Admin Dashboard</h2>
          <Table striped bordered hover responsive className="grievances-table">
            <thead>
              <tr>
                <th>Email</th>
                <th>Type</th>
                <th>Title</th>
                <th>Description</th>
                <th>Status</th>
                <th>Date Submitted</th>
              </tr>
            </thead>
            <tbody>
              {grievances.map((grievance) => (
                <tr key={grievance._id}>
                  <td>{grievance.email}</td>
                  <td>{grievance.type}</td>
                  <td>{grievance.title}</td>
                  <td>{grievance.description}</td>
                  <td>
                    <Badge variant={grievance.status === 'Pending' ? 'warning' : 'success'}>
                      {grievance.status}
                    </Badge>
                  </td>
                  <td>{new Date(grievance.createdAt).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Col>
      </Row>
    </Container>
  );
};

export default AdminDashboard;
