import React, { useState, useEffect } from 'react';
import { getGrievances, updateGrievanceStatus } from '../services/api';

const AdminDashboard = () => {
  const [grievances, setGrievances] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchGrievances = async () => {
      try {
        const response = await getGrievances();
        setGrievances(response.data);
      } catch (error) {
        console.error('Error fetching grievances:', error);
      }
    };

    fetchGrievances();
  }, []);

  const handleStatusUpdate = async (id, status) => {
    try {
      await updateGrievanceStatus(id, status);
      setGrievances(grievances.map(g => g._id === id ? { ...g, status } : g));
    } catch (error) {
      setError('Error updating status: Server error');
      console.error('Error updating status:', error);
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      {error && <div className="error">{error}</div>}
      <table>
        <thead>
          <tr>
            <th>Email</th>
            <th>Type</th>
            <th>Title</th>
            <th>Description</th>
            <th>Status</th>
            <th>Date Submitted</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {grievances.map(grievance => (
            <tr key={grievance._id}>
              <td>{grievance.email}</td>
              <td>{grievance.type}</td>
              <td>{grievance.title}</td>
              <td>{grievance.description}</td>
              <td>{grievance.status}</td>
              <td>{new Date(grievance.createdAt).toLocaleString()}</td>
              <td>
                <button onClick={() => handleStatusUpdate(grievance._id, 'Resolved')}>Mark as Resolved</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminDashboard;
